import React, { useState, useEffect } from "react";

import NotFound from "./NotFound";

import Main from "./Main";
import Cards from "./Cards";
import Details from "./Details";
import image from "../images/card-default.jpeg";
import { Route, Routes, useNavigate } from "react-router-dom";
import api from "../utils/api";

function App() {
  const [isLoaded, setIsLoaded] = useState(true);
  const [cards, setCards] = useState([]);
  const [search, setSearch] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    api
      .getInitialCards()
      .then(({ collection: { items: cardData } }) => {
        setIsLoaded(true);
        setCards(cardData);
      })
      .catch((err) => console.error(err));
  }, []);

  function handleSearchChange(e) {
    console.log(e);
    setSearch(e.target.value);
  }

  function handleSearchBtnClick() {
    api.search(search).then(({ collection: { items: searchData } }) => {
      setIsLoaded(true);
      setCards(searchData);
    });
  }

  function handleImageClick(card) {
    // console.log(`${card.data[0].title}`);
    navigate(`/${card.data[0].nasa_id}`);
  }

  return (
    <div className="page">
      <Routes>
        <Route
          path="/"
          element={
            <Main
              search={search}
              handleSearchChange={handleSearchChange}
              handleSearchBtnClick={handleSearchBtnClick}
            />
          }
        >
          <Route
            path="/:nasa_id"
            element={<Details img={image} isLoaded={isLoaded} />}
          />
          <Route
            path="/"
            element={
              <Cards
                isLoaded={isLoaded}
                cards={cards}
                search={search}
                handleImageClick={handleImageClick}
              />
            }
          />
        </Route>
        <Route path="*" element={<NotFound />} />
      </Routes>
    </div>
  );
}

export default App;

