import React from "react";
import { Text, Stylesheer } from "react-native";

export default function Card({ card, handleImageClick }) {
  function onImageClick() {
    handleImageClick(card);
  }
  return (
    <div className="card">
      <img
        className="card__image"
        src={card.links[0].href}
        alt={card.data[0].title}
        onClick={onImageClick}
      />
      <Text className="card__title">{card.data[0].title}</Text>
      <Text className="card__description">{card.data[0].description}</Text>
    </div>
  );
}
