import React, { useEffect, useState } from "react";

import { Link, useParams } from "react-router-dom";
import api from "../utils/api";

export default function Details({ img }) {
  const { nasa_id } = useParams();
  const [assetDetails, setAssetDetails] = useState([]);

  useEffect(() => {
    api
      .getImage(nasa_id)
      .then(() => {})
      .catch((err) => console.error(err));
  }, []);

  useEffect(() => {
    api
      .getDetails(nasa_id)
      .then((Data) => {
        setAssetDetails(Data);
        console.log(Data);
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <article className="details">
      <img className="details__image" src={assetDetails} alt="" />
      <h2 className="details__title">Lorem ipsum</h2>
      <p className="details__subtitle">NASA ID: {nasa_id}</p>
      <p className="details__info">{assetDetails.description}</p>
      <nav>
        <Link to="/" className="details__info link">
          Home
        </Link>
      </nav>
    </article>
  );
}
